import './App.css'

import data from '@emoji-mart/data'
import Picker from '@emoji-mart/react'

import { useState } from 'react'

const CUSTOM = [
  {
    id: 'github',
    name: 'GitHub',
    emojis: [
      {
        id: 'octocat',
        name: 'Octocat',
        keywords: ['github'],
        skins: [
          {
            src: 'https://iconape.com/wp-content/png_logo_vector/github-octocat-logo.png',
          },
        ],
        version: 1,
      },
    ],
  },
]

const CATEGORIES = [
  'frequent',
  'people',
  'nature',
  'foods',
  'activity',
  'places',
  'objects',
  'symbols',
  'flags',
  'github',
]

function App() {
  const [numCategories, setNumCategories] = useState(1)
  const [showPicker, setShowPicker] = useState(true)
  const categories = CATEGORIES.slice(0, numCategories)

  const increment = () => {
    console.log(categories)
    if (numCategories === CATEGORIES.length) return
    setNumCategories((prev) => prev + 1)
  }

  const decrement = () => {
    if (numCategories === 1) return
    setNumCategories((prev) => prev - 1)
  }

  const toggle = () => {
    setShowPicker((prev) => !prev)
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>
          <button onClick={increment}>Increment</button>
          <button onClick={decrement}>Decrement</button>
          <button onClick={toggle}>Toggle</button>
        </p>
        <h3>{numCategories}</h3>
        <p>{categories.join(', ')}</p>
        {showPicker && (
          <Picker
            data={data}
            custom={CUSTOM}
            categories={categories}
            // maxFrequentRows={0}
            noCountryFlags={true}
            onEmojiSelect={console.log}
          />
        )}
      </header>
    </div>
  )
}

export default App
