import './App.css'

import Picker from '@emoji-mart/react'
import { useState } from 'react'

const custom = [
  {
    id: 'github',
    name: 'GitHub',
    emojis: [
      {
        id: 'octocat',
        name: 'Octocat',
        keywords: ['github'],
        skins: [
          {
            src: 'https://iconape.com/wp-content/png_logo_vector/github-octocat-logo.png',
          },
        ],
        version: 1,
      },
    ],
  },
]

function App() {
  const [numCategories, setNumCategories] = useState(1)
  const [showPicker, setShowPicker] = useState(true)
  const categories = [
    'people',
    'nature',
    'foods',
    'activity',
    'places',
    'objects',
  ].slice(0, numCategories)

  return (
    <div className="App">
      <header className="App-header">
        <Picker custom={custom} maxFrequentRows={0} noCountryFlags={true} />
      </header>
    </div>
  )
}

export default App
