import './App.css'

import data from '@emoji-mart/data'
import Picker from '@emoji-mart/react'

import { useState } from 'react'

const custom = [
  {
    id: 'github',
    name: 'GitHub',
    emojis: [
      {
        id: 'octocat',
        name: 'Octocat',
        keywords: ['github'],
        skins: [
          {
            src: 'https://iconape.com/wp-content/png_logo_vector/github-octocat-logo.png',
          },
        ],
        version: 1,
      },
    ],
  },
]

function App() {
  const [numCategories, setNumCategories] = useState(4)
  const [showPicker, setShowPicker] = useState(true)
  const categories = [
    'people',
    'nature',
    'foods',
    'activity',
    'places',
    'objects',
  ].slice(0, numCategories)

  const increment = () => {
    if (numCategories === 6) return
    setNumCategories((prev) => prev + 1)
  }

  const decrement = () => {
    if (numCategories === 1) return
    setNumCategories((prev) => prev - 1)
  }

  const toggle = () => {
    setShowPicker((prev) => !prev)
  }

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={increment}>Increment</button>
        <button onClick={decrement}>Decrement</button>
        <button onClick={toggle}>Toggle</button>
        <h3>{numCategories}</h3>
        <p>{categories.join(', ')}</p>
        {showPicker && (
          <Picker
            data={data}
            custom={custom}
            categories={categories}
            maxFrequentRows={0}
            noCountryFlags={true}
            onEmojiSelect={console.log}
          />
        )}
      </header>
    </div>
  )
}

export default App
